    render() {

        return (
          <div className="app conteiner " id="contenidorPrincipal">
            <div className="container">
              <form id="formInici">
                {this.setState.poblacio}
                <div className="form-row justify-content-center">
                  <div className="form-group col-md-4">
                    <label>Nom restaurant</label>
                    <input ref={this.nom} type="text" className="form-control" onClick={this.establiment} />
                  </div>
                  <SelectPoblacio especialitat = {this.especialitat}/>
                  <SelectEspecialitats poblacio={this.poblacio}/>
                </div>
              </form>
            </div>
            <div className="container">
              <Cards establiment={this.establiment} poblacio={this.poblacio} especialitat = {this.especialitat}/>
            </div>
          </div>
    
        );
      }
}
export default Home;
