import { camelCase } from 'jquery';
import React, { Component } from 'react';
import axios from 'axios';


class SelectPoblacio extends React.Component {

    constructor(props) {
        super(props);

        this.state={
            poblacio:[],
            poblacio
        }
    
    }

    state ={
        poblacioSelect:"",
        poblacio: []
    }

    changePoblacio = (e) => {
        this.setState({
          poblacioSelect: e.target.value
        })
        this.props.onChange(e.target.value)
      }
    componentDidMount() {
        axios.post(`http://localhost/projecte/poblacio.php`)
            .then(res => {
                const poblacio = res.data;
                this.setState({ poblacio });
            })
    }

    render() {

        return (
            <div class="form-group col-md-3">
                <label >Localitat</label>
                <select id="poblacio" class="form-control" name="poblacio"  onChange={this.changePoblacio} >
                    <option selected>Selecciona</option>
                    {this.state.poblacio.map(poblacio =>
                        <option key={poblacio.id} value={poblacio.nom}>{poblacio.nom}</option>
                    )}
                </select>
            </div>

        );
    }
}

export default SelectPoblacio;
