import { camelCase } from 'jquery';
import React, { Component } from 'react';
import axios from 'axios';


class SelectPoblacio extends React.Component {

    restaurant = React.createRef();
    especialitat = React.createRef();
    localitzacio = React.createRef();

    constructor(props) {
        super(props);
    }

    state = {
        poblacio: []
    }
    componentDidMount() {
        axios.post(`http://localhost/projecte/poblacio.php`)
            .then(res => {
                const poblacio = res.data;
                this.setState({ poblacio });
            })
    }

    render() {
        return (
            <div class="form-group col-md-3">
                <label >Localitat</label>
                <select id="poblacio" class="form-control" name="poblacio">
                    <option selected>Selecciona</option>
                    {this.state.poblacio.map(poblacio =>
                        <option key={poblacio.id}>{poblacio.nom}</option>
                    )}
                </select>
            </div>

        );
    }
}

export default SelectPoblacio;
