import React, { Component } from 'react';
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {faPhoneAlt} from '@fortawesome/free-solid-svg-icons';
import {faEnvelope} from '@fortawesome/free-solid-svg-icons';
import axios from 'axios';

import ReactPaginate from "react-paginate";

class Cards extends React.Component {

    state = {
        establiment: [],
    
    }
    componentDidMount() {
        const url = 'http://localhost/projecte/establiment.php';
        axios.post(url).then(response => response.data)
            .then((data) => {
                this.setState({ establiment: data })
                console.log(this.state.establiment)
            })
    }

    componentEspecialitat(){
        axios.post(`http://localhost/projecte/filtrarCategoriesPerEstbl`)
        .then(res => {
            const especialitat = res.data;
            this.setState({ especialitat });
        })
    }

    motraFiltre = () =>{
        const filtre = this.props.filtre;


        console.log(filtre);
    }

    render() {
        if (this.props.filtre == null) {
            return (
                <div className="container">
                    <div className="row">
                        {this.state.establiment.map((establiment) => (
                            <div className="col-md-4">
                                {this.props.establiment}
                                <div className="card h-100">
                                    <img class="card-img-top" src={`http://localhost/projecte/img/${establiment.nom}`}></img>
                                    <div className="card-body">
                                        <h5 className="card-title">
                                            {establiment.establiment}</h5>
                                        <p className="card-title">
                                            <FontAwesomeIcon icon={faPhoneAlt} />
                                            {establiment.telefon}
                                        </p>
                                        <p className="card-title">
                                            <FontAwesomeIcon icon={faEnvelope} size="1px" />
                                            {establiment.correu_electronic}
                                        </p>
                                        <a href="#" class="btn btn-danger">Visita</a>
                                    </div>
                                </div>
                            </div>
                        ))}
                    </div>
                </div>
            )
        } else {
            return (
                <div className="container">
                    <div className="row">
                        {this.props.filtre.map((establiment) => (
                            <div className="col-md-4">
                                {this.props.establiment}
                                <div className="card h-100">
                                    <img class="card-img-top" src={`http://localhost/projecte/img/${establiment.foto}`}></img>
                                    <div className="card-body">
                                        <h5 className="card-title">
                                            {establiment.nom}</h5>
                                        <p className="card-title">
                                            <FontAwesomeIcon icon={faPhoneAlt} />
                                            {establiment.telefon}
                                        </p>
                                        <p className="card-title">
                                            <FontAwesomeIcon icon={faEnvelope} size="1px" />
                                            {establiment.correu_electronic}
                                        </p>
                                        <a href="#" class="btn btn-danger">Visita</a>
                                    </div>
                                </div>
                            </div>
                        ))}
                    </div>
                </div>

            );
        }
    }

    render() {

        return (
            <div className="container">
                {this.motraFiltre()}
                {this.props.establiment}
                <div className="row">
                    {this.state.establiment.map((establiment) => (
                        <div className="col-md-4">
                            {this.props.establiment}
                            <div className="card h-100">
                                <img class="card-img-top" src={`http://localhost/projecte/img/${establiment.nom}`}></img>
                                <div className="card-body">
                                    <h5 className="card-title">
                                        {establiment.establiment}</h5>
                                    <p className="card-title">
                                    <FontAwesomeIcon icon= {faPhoneAlt}/>
                                        {establiment.telefon}
                                    </p>
                                    <p className="card-title">
                                    <FontAwesomeIcon icon= {faEnvelope} size="1px"/>
                                        {establiment.correu_electronic}
                                    </p>
                                    <a href="#" class="btn btn-danger">Visita</a>
                                </div>
                            </div>
                        </div>
                    ))}
                </div>
            </div>

        );
    }

}

export default Cards;
