import React, { Component } from 'react';
import Sercador from '../Components/'
import SelectPoblacio from '../Components/SelectEspecialitats';
import SelectEspecialitats from "../Components/SelectPoblacio";
import Cards from "../Components/Cards"

class Home extends React.Component {
   
  constructor(props){
    super(props);

    this.state = {
      poblacio:"",
      especialitat:""
    }
  
  }

  nom = React.createRef();

  obtenirValors = (e) =>{
    e.preventDefault();
    console.log(this.nom.current.value);
  }
  
    render() {

        return (
          <div className="app conteiner " id="contenidorPrincipal">
            <div className="container">
              <form id="formInici" onSubmit={this.obtenirValors}>
                <div className="form-row justify-content-center">
                 <Sercador />
                  <SelectPoblacio />
                  <SelectEspecialitats />
                  <div className="form-group col-md-4">
                    <input type="submit" className="btn btn-danger" value="Cerca"/>
                  </div>
                </div>
              </form>
            </div>
            <div className="container">
              <Cards />
            </div>
          </div>
    
        );
      }
}
export default Home;
