import { camelCase } from 'jquery';
import React, { Component } from 'react';
import axios from 'axios';
import PropTypes from 'prop-types';
import { faSearch } from '@fortawesome/free-solid-svg-icons';


class SelectPoblacio extends React.Component {

    constructor(props) {
        super(props);

        this.state={
            selectPoblacio:"",
            poblacio:[],
         
            
        }

        this.componentDidMount = this.componentDidMount.bind(this);
        this.changePoblac = this.changePoblac.bind(this);
    
    }

    changePoblac(e){
        this.setState({selectPoblacio:e.target.value})
    }
    
      
    componentDidMount() {
        axios.post(`http://localhost/projecte/poblacio.php`)
            .then(res => {
                const poblacio = res.data;
                this.setState({ poblacio });
            })
    }

    render() {

        const {changePoblacio}=this.props
        return (
            <div className="form-group col-md-3">
                 <h4>{this.state.selectPoblacio}</h4>
                <label >Localitat</label>
                <select id="poblacio" class="form-control" name="poblacio"  onChange={changePoblacio} >
                    <option selected>Selecciona</option>
                    {this.state.poblacio.map(poblacio =>
                        <option value={poblacio.nom}>{poblacio.nom}</option>
                    )}
                </select>
            </div>

        );
    }
}
faSearch.PropTypes = {
    changePoblacio : Pro
}

export default SelectPoblacio;
