import React, { Component } from 'react';
import axios from 'axios';

class Cards extends React.Component {

    restaurant = React.createRef();
    especialitat = React.createRef();
    localitzacio = React.createRef();

    state = {
        establiment: []
    }
    componentDidMount() {
        axios.get(`http://localhost/projecte/establiment.php`)
            .then(res => {
                const establiment = res.data;
                this.setState({ establiment });
            })
    }

    render() {
        return (
            <div class="form-group col-md-3">
                <label >Especialitat</label>
                <select id="especialitats" class="form-control" name="especialitats">
                    <option selected>Selecciona</option>
                    {this.state.establiment.map(establiment =>
                        <option key={establiment.id}>{establiment.nom}</option>
                    )}
                </select>
            </div>

        );
    }
}

export default Cards;

<div class="card" style="width: 18rem;">
    <img class="card-img-top" src="..." alt="Card image cap" />
    <div class="card-body">
        {this.state.establiment.map(establiment =>
            <h5 class="card-title">{establiment.nom}</h5>,
            <p class="card-text">Some quick example text to build on the card title and make up the bulk of the card's content.</p>,
            <a href="#" class="btn btn-primary">Go somewhere</a>
        )}

    </div>
</div>
