import React, { Component } from 'react';
import axios from 'axios';

class Cards extends React.Component {

    state = {
        establiment: []
    }

    componentDidMount() {
        axios.get(`http://localhost/projecte/establiment.php`)
            .then(res => {
                const establiment = res.data;
                this.setState({ establiment });
            })
    }

    render() {
        return (
            <div class="card" style="width: 18rem;">
                <img class="card-img-top" src="https://www.google.com/url?sa=i&url=https%3A%2F%2Fcnnespanol.cnn.com%2Fgallery%2Ffotos-15-restaurantes-nuevos-para-probar-en-2019%2F&psig=AOvVaw07q8ALbc_acIksHrz4qYYZ&ust=1613579167897000&source=images&cd=vfe&ved=0CAIQjRxqFwoTCKiQz4Hp7u4CFQAAAAAdAAAAABAJ" alt="Card image cap"/>
                    <div class="card-body">
                    {this.state.establiment.map(establiment =>
                        <h5 class="card-title">{establiment.nom}</h5>,
                        <p class="card-text">{establiment.descripcio}</p>,
                        <a href="#" class="btn btn-danger">Visita</a>
                    )}
                    </div>
            </div>
        );
    }
}

export default Cards;
