import axios from 'axios';
import {CircleMarker, TileLayer,CircleProps,MapContainer} from 'react-leaflet'

class Map extends React.Component {
    state = {
        coordenades: [],
    
    }
    componentDidMount() {
        const url = 'http://localhost/projecte/readCoordenades.php';
        axios.post(url).then(response => response.data)
            .then((data) => {
                this.setState({ coordenades: data })
            })
    }

    render() {
        const position = [39.60017583077754, 2.9943578633572976]
        return (
            <MapContainer center={position} zoom={9}>
                <TileLayer
                   
                />
                {
                    data.city.map((city,k) => {
                        return (
                            <CircleMarker
                              key={k}
                              center={[city["coordinates"][1], city["coordinates"][0]]}
                              radius={20 * Math.log(city["population"] / 10000000)}
                              fillOpacity={0.5}
                              stroke={false}
                            >
                              <Tooltip direction="left" offset={[-8, -2]} opacity={1}>
                                <span>{city['name'] + ': ' + 'Population' + ' ' + city['population']}</span>
                              </Tooltip>
                            </CircleMarker>
                            )
                        })
                }
            </MapContainer>
        );
    }

}

export default Map;
