import React, { Component } from 'react';
import SelectPoblacio from '../Components/SelectEspecialitats';
import SelectEspecialitats from "../Components/SelectPoblacio";
import Cards from "../Components/Cards"
import Sercador from "../Components/Sercador"
import Map from "../Components/Map";


class Home extends React.Component {

  state = {
    poblacio: '',
    especialitat: '',
    establiment: '',
    filtre: [],
    preferredLocale: "es"
  }

  changeLanguage = ({ currentTarget: { id } }) => {
    this.setState({
      preferredLocale: id
    });
  };
  consultaApi = () => {
    const url = `http://localhost/projecte/filtre.php?nom=${this.state.establiment}&poblacio=${this.state.poblacio}&categoria=${this.state.especialitat}`;
    console.log(url)

    fetch(url)
      .then(resposta => resposta.json())
      .then(resultat => this.setState({ filtre: resultat }))
  }
  establiment = (establiment) => {
    this.setState({
      establiment
    }, () => {
      this.consultaApi();
    })
  }

  poblacio = (poblacio) => {

    this.setState({
      poblacio
    }, () => {
      this.consultaApi();
    })

  }

  especialitat = (especialitat) => {
    this.setState({
      especialitat
    }, () => {
      this.consultaApi();
    })

  }

  render() {

    return (
      <div className="app conteiner " id="contenidorPrincipal">
        <div className="container">
          <form id="formInici">
            <div className="form-row justify-content-center">
              <Sercador establiment={this.establiment}/>
              <SelectPoblacio especialitat={this.especialitat} changeLanguage={this.changeLanguage} />
              <SelectEspecialitats poblacio={this.poblacio} changeLanguage={this.changeLanguage}/>
            </div>
          </form>
        </div>
        <div className="container">
          <Cards filtre={this.state.filtre} />
        </div>
        <div className="container">
          <Map />
        </div>
      </div>

    );
  }

}
export default Home;
