import React, { Component } from 'react';
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {faPhoneAlt} from '@fortawesome/free-solid-svg-icons';
import {faEnvelope} from '@fortawesome/free-solid-svg-icons';
import axios from 'axios';


class Cards extends React.Component {

    state = {
        establiment: [],
    
    }
    componentDidMount() {
        const url = 'http://localhost/projecte/establiment.php';
        axios.post(url).then(response => response.data)
            .then((data) => {
                this.setState({ establiment: data })
                console.log(this.state.establiment)
            })
    }

    retornaSenseFiltre(){

    }
    
    render() {

        if (this.props.filtre.length === 0) {
            return (
                <div className="container-card">
                    <div className="row">
                        {this.state.establiment.map((establiment) => (
                            <div className="col-md-4">
                                <div className="card h-100" key={establiment.id}>
                                    <img className="card-img-top" src={`http://localhost/projecte/img/${establiment.nom}`} style={{height:200, width}}></img>
                                    <div className="card-body">
                                        <h5 className="card-title">
                                            {establiment.establiment}</h5>
                                        <p className="card-title">
                                            <FontAwesomeIcon icon={faPhoneAlt} />
                                            {establiment.telefon}
                                        </p>
                                        <p className="card-title">
                                            <FontAwesomeIcon icon={faEnvelope}/>
                                            {establiment.correu_electronic}
                                        </p>
                                        <a href="#" className="btn">Visita</a>
                                    </div>
                                </div>
                            </div>
                        ))}
                    </div>
                </div>
            )
        } else {
            return (
                <div className="container">
                    <div className="row">
                        {this.props.filtre.map((establiment) => (
                            <div className="col-md-4">
                                {this.props.establiment}
                                <div className="card h-100" key={establiment.id}>
                                    <img className="card-img-top" src={`http://localhost/projecte/img/${establiment.foto}`} style={{height:250}}></img>
                                    <div className="card-body">
                                        <h5 className="card-title">
                                            {establiment.nom}</h5>
                                        <p className="card-title">
                                            <FontAwesomeIcon icon={faPhoneAlt} />
                                            {establiment.telefon}
                                        </p>
                                        <p className="card-title">
                                            <FontAwesomeIcon icon={faEnvelope}/>
                                            {establiment.correu_electronic}
                                        </p>
                                        <p className="card-title">
                                            {establiment.p}
                                        </p>
                                        <a href="#" className="btn ">Visita</a>
                                    </div>
                                </div>
                            </div>
                        ))}
                    </div>
                </div>

            );
        }
    }


}

export default Cards
