import React, { Component } from 'react';
import {Carrusel,Grid,Row,Col} from 'react-boodstrap'
import './App.css';
import { findRenderedDOMComponentWithClass } from 'react-dom/test-utils';

class App extends Component {
  render() {

    return (
      <div>
        <table border="1">
        <thead><tr><th>Code</th><th>Description</th><th>Price</th><th>Delete?</th></tr></thead>
        <tbody>
        {this.state.products.map(item => {
          return (
            <tr key={item.code}>
              <td>
                {item.code}  
              </td>
              <td>
                {item.description}
              </td>
              <td>
                {item.price}
              </td>   
              <td>
                <button onClick={()=>this.delectProduct(item.code)}>Delete</button>
              </td>           
            </tr>
          )
        })}    
        </tbody>    
        </table>
      </div>
    );
  }

  delectProduct(code) {
    var temp = this.state.products.filter((item) => item.code !== code);
    this.setState({
      products: temp
    })
  }
}

export default App;
