import { camelCase } from 'jquery';
import React, { Component } from 'react';
import axios from 'axios';
import PropTypes from 'prop-types';
import { faSearch } from '@fortawesome/free-solid-svg-icons';


class SelectPoblacio extends React.Component {

    constructor(props) {
        super(props);

        this.state = {
            selectPoblacio: "",
            poblacio: []
        }

    }

    p = React.createRef();

    componentDidMount() {
        axios.post(`http://localhost/projecte/poblacio.php`)
            .then(res => {
                const poblacio = res.data;
                this.setState({ poblacio });
            })
    }

    obteniPoblacio = (e) =>{
        e.preventDefault();
        console.log(this.p.current.value);
        this.props.poblacio()
      }

    render() {


        return (

            <div className="form-group col-md-3">
                <label >Localitat</label>
                <select id="poblacio" className="form-control" name="poblacio" ref={this.p} onClick={this.obteniPoblacio} >
                    <option selected>Selecciona</option>
                    {this.state.poblacio.map(poblacio =>
                        <option value={poblacio.nom}>{poblacio.nom}</option>
                    )}
                </select>
            </div>

        );
    }

}

export default SelectPoblacio;
