import React, { Component } from 'react';
import axios from 'axios';
import { TileLayer, Marker, MapContainer, Popup } from 'react-leaflet'

class Map extends React.Component {
    state = {
        coordenades: [],

    }
    componentDidMount() {
        const url = 'http://localhost/projecte/readCoordenades.php';
        axios.post(url).then(response => response.data)
            .then((data) => {
                this.setState({ coordenades: data })
                console.log(this.state.coordenades)
            })
    }

    render() {
        const position = [39.60017583077754, 2.9943578633572976]
        return (
            <div className="container">
                <MapContainer center={position} zoom={9}>
                    <TileLayer
                        attribution='&copy; <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
                        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                    />
                    {this.state.coordenades.map((c) => {
                            return (
                                <Marker
                                    position={[c.lat, c.lng]}
                                >
                                    <Popup >
                                        {c.nom}
                                    </Popup>
                                </Marker>
                            )
                        })
                    }
                </MapContainer>
            </div>
        );
    }

}

export default Map;
