import { camelCase } from 'jquery';
import React,{Component} from 'react';

class IniciSesio extends Component{
    //Llitgir valor des inputs//

    //nom = React.createRef();
    //clau = React.createRef();

    render(){
        return(
            <form>
                <div className="row">
                    <div className="form-group col-md-8">
                        <input ref={this.nom} type="email" className="form-control form-control-lg" placeholder="Correu Electronic"/>
                    </div>
                    <div className="form-group col-md-8">
                        <input ref={this.clau} type="password" className="form-control form-control-lg" placeholder="Clau"/>
                    </div>
                    <div className="form-group col-md-8">
                        <input type="submit" className="btn btn-lg btn-danger btn-block" value="Inicia Sesio"/>
                    </div>

                </div>
            </form>
        );
    }
}

export default IniciSesio;
