import { camelCase } from 'jquery';
import React, { Component } from 'react';
import axios from 'axios';


class Inici extends Component {

    restaurant = React.createRef();
    especialitat = React.createRef();
    localitzacio = React.createRef();

    state = {
        persons: []
      }
    
      componentDidMount() {
        axios.get(`https://jsonplaceholder.typicode.com/users`)
          .then(res => {
            const persons = res.data;
            this.setState({ persons });
          })
      }
    
      render() {
        return (
          <ul>
            { this.state.persons.map(person => <li>{person.name}</li>)}
          </ul>
        )
      }

    render() {
        return (
            <form>
                <div className="form-row">
                    <div className="form-group col-md-3">
                        <label>Nom restaurant</label>
                        <input ref={this.nom} type="email" className="form-control" placeholder="" />
                    </div>
                    <div class="form-group col-md-3">
                        <label >Localitat</label>
                        <select id="poblacio" class="form-control" name="poblacio">
                            <option selected>Selecciona</option>
                        </select>
                    </div>
                    <div class="form-group col-md-3">
                        <label>Especialitat</label>
                        <select id="especialitat" class="form-control" name="especialitat">
                            <option selected>Selecciona</option>
                        </select>
                    </div>
                    <div className="form-group col-md-3">
                        <input type="submit" className="btn btn-lg btn-danger" value="Cercar" />
                    </div>

                </div>
            </form>
        );
    }
}

export default Inici;
