import React, { Component } from 'react';
import SelectPoblacio from '../Components/SelectEspecialitats';
import SelectEspecialitats from "../Components/SelectPoblacio";
import Cards from "../Components/Cards"

class Home extends React.Component {

  constructor(props){
    super(props);

    this.state = {
      texteNom:"",
      selectPoblacio:"",
      especialitat:""
    }
    this.changePoblacio = this.changePoblacio.bind(this);
  }
  changePoblacio(e){
    this.setState({selectPoblacio:e.target.value})
  }
    render() {

        return (
          <div className="app conteiner " id="contenidorPrincipal">
            <div className="container">
              <form id="formInici">
                <div className="form-row justify-content-center">
                  <div className="form-group col-md-4">
                    <label>Nom restaurant</label>
                    <input type="text" className="form-control" />
                  </div>
                  <SelectPoblacio/>
                  <h4>{this.state.selectPoblacio}</h4>
                  <SelectEspecialitats />
                  <div className="form-inline">
                    <input type="submit" className="btn btn-danger" value="Cercar"/>
                  </div>
    
                </div>
              </form>
            </div>
            <div className="container">
              <Cards />
            </div>
          </div>
    
        );
      }
}
export default Home;
