import React, { Component } from 'react';
import axios from 'axios';

class Cards extends React.Component {

    restaurant = React.createRef();
    especialitat = React.createRef();
    localitzacio = React.createRef();

    state = {
        establiment: []
    }
    componentDidMount() {
        const url = 'http://localhost/projecte/establiment.php';
        axios.get(url).then(response => response.data)
            .then((data) => {
                this.setState({ establiment: data })
                console.log(this.state.establiment)
            })
    }

    render() {

        return (
            <div className="container">
                <div className="col-xs-5">
                    {this.state.establiment.map((establiment) => (
                        <div className="card">
                            <img class="card-img-top" src="https://cnnespanol.cnn.com/wp-content/uploads/2019/01/Galerias-nuevos-restaurantes-2019-CNN-1.jpg?quality=100&strip=info" style={{fontSize: '800px'}} alt="Card image cap"></img>
                            <div className="card-body">
                                <h5 className="card-title">{establiment.nom}</h5>
                                <p className="card-title">
                                    {establiment.descripcio}
                                </p>
                                <a href="#" class="btn btn-danger">Visita</a>
                            </div>
                        </div>
                    ))}
                </div>
            </div>
        );
    }

}

export default Cards;
