import React, { Component } from 'react';
import axios from 'axios';

class SelectEspecialitats extends React.Component {
    constructor(props) {
        super(props);
    }

    state = {
        especialitat: [],
        
    }
    componentDidMount() {
        axios.post(`http://localhost/projecte/categoria.php`)
            .then(res => {
                const especialitat = res.data;
                this.setState({ especialitat });
            })
    }

    e = React.createRef();

    obteniEspecialitat = (e) =>{
        e.preventDefault();
    
        this.props(this.e.current.value);
      }

    render() {
        return (
            <div class="form-group col-md-3">
                <label >Especialitat</label>
                <select id="especialitats" className="form-control" name="especialitats" ref={this.e} onClick={this.obteniEspecialitat}>
                    <option selected>Selecciona</option>
                    {this.state.especialitat.map(especialitat =>
                        <option value={especialitat.id}>{especialitat.nom}</option>
                    )}
                </select>
            </div>

        );
    }
}

export default SelectEspecialitats;
