import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import { findRenderedDOMComponentWithClass } from 'react-dom/test-utils';

class App extends Component {

  constructor(props) {
    super(props)
    this.state = {
      products: [{
        ode: 1,
        description: 'potatoes',
        price: 12.52
      }, {
        code: 2,
        description: 'oranges',
        price: 21
      }, {
        code: 3,
        description: 'bananas',
        price: 18.20
      }]

    }
    this.delectProduct = this.delectProduct.bind(this);
  }

  render() {

    return (
      <div>
        <table border="1">
        <thead><tr><th>Code</th><th>Description</th><th>Price</th><th>Delete?</th></tr></thead>
        <tbody></tbody>

        </table>
        <tr key={item.code}>
          <td>
            {item.code}
          </td>
          <td>
            {item.description}
          </td>
          <td>
            {item.price}
          </td>
          <td>
            <button onClick={() => this.delectProduct(item.code)}>Delete</button>
          </td>
        </tr>
      </div>
    );
  }

  delectProduct(code) {
    var temp = this.state.products.filter((item) => item.code !== code);
    this.setState({
      products: temp
    })
  }
}

export default App;
