import SelectPoblacio from '../Components/SelectEspecialitats';
import SelectEspecialitats from "../Components/SelectPoblacio";
import Cards from "../Components/Cards"

class Home extends React.Component {
   
  constructor(props){
    super(props);

    /*state = {
      establiment:"",
      poblacio:"",
      especialitat:""
    }*/
  
  }

  state = {
    establiment:"",
    poblacio:"",
    especialitat:""
  }

  nom = React.createRef();

  establiment = (e) =>{
    e.preventDefault();
    console.log(this.nom.current.value);

    this.setState({
      establiment
    })
  }

  poblacio = (e) =>{
    console.log(e);
  }

  especialitat = (e) =>{
    console.log(e);
  }
 
    render() {

        return (
          <div className="app conteiner " id="contenidorPrincipal">
            <div className="container">
              <form id="formInici">
                {this.state.establiment}
                <div className="form-row justify-content-center">
                  <div className="form-group col-md-4">
                    <label>Nom restaurant</label>
                    <input ref={this.nom} type="text" className="form-control" onClick={this.establiment} />
                  </div>
                  <SelectPoblacio especialitat = {this.especialitat}/>
                  <SelectEspecialitats poblacio={this.poblacio}/>
                </div>
              </form>
            </div>
            <div className="container">
              <Cards establiment={this.establiment} poblacio={this.poblacio} especialitat = {this.especialitat}/>
            </div>
          </div>
    
        );
      }
}
export default Home;
