import React, { Component } from 'react';
import SelectPoblacio from '../Components/SelectEspecialitats';
import SelectEspecialitats from "../Components/SelectPoblacio";
import Cards from "../Components/Cards"
import Sercador from "../Components/Sercador"
import Map from "../Components/Map";

class Home extends React.Component {

  state = {
    poblacio: '',
    especialitat: '',
    establiment: '',
    filtre:[]
  }

  consultaApi = () => {
    const url = `http://localhost/projecte/filtre.php?nom=${this.state.establiment}&poblacio=${this.state.poblacio}&categoria=${this.state.especialitat}`;
    console.log(url)

    fetch(url)
    .then( resposta => resposta.json() )
    .then( resultat => this.setState({filtre:resultat}))
  }
  establiment = (establiment) => {
    this.setState({
      establiment
    },()=>{
      this.consultaApi();
    })
  }

  poblacio = (poblacio) => {

    this.setState({
      poblacio
    },()=>{
      this.consultaApi();
    })

  }

  especialitat = (especialitat) => {
    this.setState({
      especialitat
    },()=>{
      this.consultaApi();
    })

  }

 render() {
  const position = [51.505, -0.09]
    return (
      <MapContainer center={position} zoom={13} scrollWheelZoom={false}>
    <TileLayer
      attribution='&copy; <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
      url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
    />
    <Marker position={position}>
      <Popup>
        A pretty CSS3 popup. <br /> Easily customizable.
      </Popup>
    </Marker>
  </MapContainer>
      /*
      <div className="app conteiner " id="contenidorPrincipal">
        <div className="container">
          <form id="formInici">
            <div className="form-row justify-content-center">
              <Sercador establiment={this.establiment} />
              <SelectPoblacio especialitat={this.especialitat} />
              <SelectEspecialitats poblacio={this.poblacio} />
            </div>
          </form>
        </div>
        <div className="container">
          <Cards filtre={this.state.filtre} />
        </div>
        <div className="container">
          <Map />
        </div>
      </div>*/

    );
  }
  
}
export default Home;
